const { Server } = require('@modelcontextprotocol/sdk/server/index.js');
const { StdioServerTransport } = require('@modelcontextprotocol/sdk/server/stdio.js');

const server = new Server({ name: 'iia-resources', version: '1.0.0' }, { capabilities: { tools: {} } });

server.setRequestHandler('tools/list', async () => ({
  tools: [{
    name: 'search_iia',
    description: 'Search IIA standards and guidance',
    inputSchema: {
      type: 'object',
      properties: { query: { type: 'string' } },
      required: ['query']
    }
  }]
}));

server.setRequestHandler('tools/call', async (req) => {
  const response = await fetch('https://your-vercel-url.vercel.app/api/search?q=' + encodeURIComponent(req.params.arguments.query));
  const data = await response.json();
  return { content: [{ type: 'text', text: JSON.stringify(data, null, 2) }] };
});

server.connect(new StdioServerTransport());
